import Shape from"./shape.js";export default class Star extends Shape{constructor(t,s,a,r){super("star",t,s,a,r);const h=.5*Math.PI;this.graphics.drawPolygon(this.calculateStarVertices(0,0,25,5,10,h)),this.graphics.endFill(),this.graphics.x=this.x,this.graphics.y=this.y}calculateStarVertices(t,s,a,r,h,c){const e=Math.PI/r,i=[];for(let n=0;n<2*r;n++){const r=n%2==0?a:h,o=t+Math.cos(n*e+c)*r,l=s+Math.sin(n*e+c)*r;i.push(o,l)}return i}calculateArea(){const t=2*Math.PI/10;return 5*(625*Math.sin(t)+100*Math.sin(t))/2}}